import React, { useState, useEffect } from "react";
import axios from "axios";
import Header from "../components/Header";

function Login() {
  const [csrfToken, setCsrf] = useState("");

  useEffect(() => {
    async function fetchCsrfToken() {
      const response = await fetch("/api/get-csrf-token");
      const { token } = await response.json();
      setCsrf(token);
    }
    fetchCsrfToken();
  }, []);
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const headers = {
      "X-CSRFToken": csrfToken,
    };

    try {
      // Send form data to the Django API endpoint
      await axios.post("/api/users/", { formData, headers });

      // Clear form inputs after successful submission
      setFormData({
        email: "",
        password: "",
      });
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };
  return (
    <div>
      <Header />
      <div className="relative flex flex-col justify-center overflow-hidden mt-3">
        <div className="w-full p-6 m-auto bg-white rounded-md shadow-md sm:max-w-lg">
          <h1 className="text-4xl font-bold text-center mb-5">Login</h1>
          <form className="space-y-4" onSubmit={handleSubmit}>
            <div className="form-control w-full ">
              <label className="label">Email</label>
              <input
                required
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Email address"
                className="input input-bordered w-full"
              />
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div className="form-control w-full">
              <label className="label">Password</label>
              <input
                required
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter password"
                className="input input-bordered w-full"
                minLength={6}
              />
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div className="w-1/2">
              <label className="label cursor-pointer">
                <span className="label-text">Remember me</span>
                <input
                  type="checkbox"
                  name="remember_me"
                  value={formData.remember_me}
                  onChange={handleChange}
                  checked="checked"
                  className="checkbox"
                />
              </label>
            </div>
            <div>
              <a
                href="/forgot-password"
                className="text-sm text-gray-600 hover:underline hover:text-blue-600"
              >
                Forgot password?
              </a>
            </div>
            <div>
              <button className="btn" type="submit">
                Login
              </button>
            </div>
            <div>
              <a
                href="/register"
                className="text-sm text-gray-600 hover:underline hover:text-blue-600"
              >
                Don't have an account? Sign up
              </a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
