import React, { useEffect, useState } from "react";
import axios from "axios";
import Header from "../components/Header";

function Register() {
  const [csrfToken, setCsrf] = useState("");

  useEffect(() => {
    async function fetchCsrfToken() {
      const response = await fetch("/api/get-csrf-token");
      const { token } = await response.json();
      setCsrf(token);
    }
    fetchCsrfToken();
  }, []);
  const [formData, setFormData] = useState({
    first_name: "",
    last_name: "",
    email: "",
    password: "",
  });

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Send form data to the Django API endpoint
      await axios.post("/api/users/", formData, {
        headers: {
          "X-CSRFToken": csrfToken,
        },
      });
      console.log(csrfToken);

      // Clear form inputs after successful submission
      setFormData({
        first_name: "",
        last_name: "",
        email: "",
        password: "",
      });
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  return (
    <div>
      <Header />
      <div className="relative flex flex-col justify-center overflow-hidden">
        <div className="w-full p-6 m-auto bg-white rounded-md shadow-md sm:max-w-lg">
          <h1 className="text-4xl font-bold text-center mb-5">Sign up</h1>
          <form className="space-y-4" onSubmit={handleSubmit}>
            <div className="form-control w-full">
              <label className="label">First Name</label>
              <input
                required
                type="text"
                name="first_name"
                value={formData.first_name}
                onChange={handleChange}
                placeholder="Enter First Name"
                className="input input-bordered w-full"
              />
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div className="form-control w-full">
              <label className="label">Last Name</label>
              <input
                required
                type="text"
                name="last_name"
                value={formData.last_name}
                onChange={handleChange}
                placeholder="Enter Last Name"
                className="input input-bordered w-full"
              />
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div className="form-control w-full ">
              <label className="label">Email</label>
              <input
                required
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Email address"
                className="input input-bordered w-full"
              />
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div className="form-control w-full">
              <label className="label">Password</label>
              <input
                required
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter password"
                className="input input-bordered w-full"
                minLength={6}
              />
              <label className="label-text-alt">
                Atleast 6 characters long
              </label>
              <label className="label-text-alt text-red-500 hidden">
                Error
              </label>
            </div>
            <div>
              <button className="btn" type="submit">
                Register
              </button>
            </div>
            <div>
              <a
                href="/login"
                className="text-sm text-gray-600 hover:underline hover:text-blue-600"
              >
                Already have an account? Login
              </a>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Register;
